	public static double mic(double[] x, double[] y, double maxClumpColumnRatio){
		if(x == null){
			throw new NullPointerException("x passed to mic was null");
		}
		if(y == null){
			throw new NullPointerException("y passed to mic was null");
		}
		if(x.length != y.length){
			throw new IllegalArgumentException("x and y must have the same length");
		}
		
		
		double[][] a=ApproxMINECharacteristicMatrix(x, y, (int)Math.round(Math.pow(x.length,0.6)), maxClumpColumnRatio);
		assert(a != null && a.length > 0 && a[0] != null && a[0].length > 0);
		
		double mic = a[0][0];
		for(int i = 0; i < a.length; ++i){
			double[] row = a[i];
			for(int j = 0; j < row.length; ++j){
				mic = Math.max(mic, row[j]);
			}
		}
		return mic;
	}
